import { createSlice } from "@reduxjs/toolkit";

const taskSlice = createSlice({
  name: "tasks",
  initialState: { tasks: {} },
  reducers: {
    addTask: (state, action) => {
      const { date, text } = action.payload;
      const newTask = {
        id: Date.now(),
        text: text,
        date: date,
      };
      // if there is  no key/value for that date
      if (!state.tasks[date]) {
        // create the key, with value as the new task
        state.tasks[date] = [newTask];
      } else {
        state.tasks[date].push(newTask);
      }
    },
    editTask: (state, action) => {
      const { date, text, id } = action.payload;
      const editTask = state.tasks[date].find((task) => task.id === id);
      editTask ? (editTask.text = text) : console.log("Task not found");
    },
    deleteTask: (state, action) => {
      const { id, date } = action.payload;
      const taskIndex = state.tasks[date].findIndex((task) => task.id === id);
      taskIndex !== -1
        ? state.tasks[date].splice(taskIndex, 1)
        : console.log("Task not found");
    },
  },
});

export const { addTask, editTask, deleteTask } = taskSlice.actions;

// Export the reducer
export default taskSlice.reducer;
