// App.js
import React, { useState } from "react";
import { Provider } from "react-redux";

import taskSlice from "./taskSlice"; // Assuming you have a rootReducer
import store from "./store";
import Calendar from "./components/Calendar";
import TaskList from "./components/TaskList";
import AddTask from "./components/AddTask";
import EditTask from "./components/EditTask";
import DeleteTask from "./components/DeleteTask";

const App = () => {
  const [selectedDay, setSelectedDay] = useState(new Date());
  const [selectedTask, setSelectedTask] = useState(null);

  const handleDateSelect = (date) => {
    setSelectedDay(date);
    setSelectedTask(null);
  };

  return (
    <Provider store={store}>
      <div>
        <h1>Your Task Manager App</h1>
        <Calendar onDateSelect={handleDateSelect} />

        <TaskList selectedDay={selectedDay} />

        <AddTask selectedDay={selectedDay} />

        {selectedTask && (
          <>
            <EditTask selectedDay={selectedDay} task={selectedTask} />
            <DeleteTask selectedDay={selectedDay} task={selectedTask} />
          </>
        )}
      </div>
    </Provider>
  );
};

export default App;
