import React from "react";
import { useSelector } from "react-redux";

const TaskList = ({ day }) => {
  const tasks = useSelector((state) => state.tasks.tasks[day] || []);

  return (
    <div>
      <h2>Tasks for {day}</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>{task.text}</li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
